express的代码 ，只写了一个get -- 然后返回数据

var express = require('express');    
var app = express();
var ds = require("./s_data.js");

app.get('/note/', function(req, res){   
    res.contentType('json');//返回的数据类型
    res.send("welcome to my note!");//给客户端返回一个json格式的数据  
});

app.get('/note/query/*', function(req, res){  
	ds.queryNote(req.query.id,function(results){
	  res.contentType('json');//返回的数据类型
	  res.setHeader('Content-Type', 'text/plain');
	  res.send(JSON.stringify({ code:0 , datas:results}));//给客户端返回一个json格式的数据  
	}, function(err){
		res.send(JSON.stringify({ code:-1 , err:err}));
	})
});
app.listen(10018);    
console.log('Listening on port 10018');   

