var execFile = require('child_process').execFile;
var dwebp = require('dwebp-bin').path;
var fs = require('fs');

var files = fs.readdirSync('fuckpic');

for(var i in files){
var p = files[i];
if(p.indexOf('.webp') != -1){
execFile(dwebp, ['./fuckpic/'+p, '-o', './fuckpic/'+p.replace('.webp', '.png')], function (error) {
if (error) {
throw error;
}

console.log('Image was converted');
});
}
}

