//post
var bodyParser = require('body-parser');

//session
var session = require('express-session')

app.use(bodyParser.urlencoded({extended: false}));
app.use(bodyParser.json());

app.use(session({ secret: 'keyboard cat', cookie: { maxAge: 60000000 }, name:"login_session"}))

//设置跨域访问
app.all('*', function(req, res, next) {
res.header("Access-Control-Allow-Origin", "*");
res.header("Access-Control-Allow-Headers", "X-Requested-With");
res.header("Access-Control-Allow-Methods","PUT,POST,GET,DELETE,OPTIONS");
res.header("X-Powered-By",' 3.2.1')
res.header("Content-Type", "application/json;charset=utf-8");
next();
});

app.post('/login', function (req, res) {
console.log(req.body);
if(!req.body.name || req.body.password){
res.send(JSON.stringify({ code:-2 , detail:'param miss'}));
return;
}
ds.queryNote(req.body.name,function(results){
res.contentType('json');
res.setHeader('Content-Type', 'text/plain');

//no find
if(results.length &lt; 1){
res.send(JSON.stringify({ code:-4 , detail:'no this user exist'}));
}else{
var u = results[0];
if(req.body.password != u.password){
res.send(JSON.stringify({ code:-3 , detail:'password incorrect'}));
}else{
var sess = req.session;
if(!sess.islogin){
sess.islogin = 1;
}
res.send(JSON.stringify({ code:0 , datas:results}));
}
}
}, function(err){
res.send(JSON.stringify({ code:-1 , err:err}));
})

});

app.listen(10018);

